function toggleMobileMenu(){const e=document.querySelector(".hamburger"),t=document.querySelector(".nav__menu");e.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.toggle("show")})}document.addEventListener("DOMContentLoaded",()=>{toggleMobileMenu();const t=document.querySelectorAll(".animate"),n=new IntersectionObserver(e=>{e.forEach(e=>{if(e.isIntersecting)for(let e=0;e<t.length;e++)t[e].classList.add("fadein")})});t.forEach(e=>{n.observe(e)})});